#include <stdio.h>
#include <time.h>
#include <string.h>


int main(void) {

    clock_t start, stop;

    start = clock();

    char big_strnum[] = "";


    long high = 0;
    size_t str_len = strlen (big_strnum);
    long total = 1;

    int i, j, x;
    for (i=0; i < str_len - 5; i++) {
        j = 0;
        while (j < 5) {
            x = big_strnum[i + j] - '0';
            total = total * x;
            j++; 
        }
        if (total > high) {
            high = total;
        }
        total = 1;
    }
    stop = clock();
    printf ("Answer: %ld\n", high);
    printf ("Time: %f\n", ((float)stop - (float)start) / CLOCKS_PER_SEC);
    return 0;
}

